import React from 'react';
import "../css/experiences.css"; // Add a specific CSS file for Experiences

const experiences = [
  {
    company: "Performalytic",
    date: "May 2023 - July 2023",
    position: "Software Developer Intern (NodeJS, ReactJS)",
    responsibilities: [
      "Implemented OAuth and JWT for secure user authentication and authorization in web applications, using bcrypt for password hashing to enhance security.",
      "Integrated SendGrid API for password reset functionality, ensuring seamless user experience for account recovery.",
      "Contributed to database architecture and implemented data management solutions using MySQL, performing queries and data manipulations to support application functionalities.",
      "Utilized Git for version control to facilitate collaboration within the development team, ensuring smooth integration of code changes."
    ]
  },
  // Add more experiences here...
];

function Experiences() {
  return (
    <div className="max-w-4xl mx-auto my-8 p-4">
      <h2 className="text-2xl font-bold mb-6">Experiences</h2>
      <ul className="space-y-4">
        {experiences.map((experience, index) => (
          <li key={index} className="p-4 border rounded-lg shadow-md bg-white">
            <h3 className="text-xl font-semibold">{experience.position} at {experience.company}</h3>
            <p className="text-sm text-gray-600 mb-2"><strong>Date:</strong> {experience.date}</p>
            <ul className="list-disc pl-5">
              {experience.responsibilities.map((responsibility, resIndex) => (
                <li key={resIndex} className="text-gray-800">{responsibility}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Experiences;
